def generate_english_connector_list():
    return english_conjunctions \
        + english_prepositions \
        + english_determinants \
        + english_pronouns


def generate_capitalized_english_connector_list():
    capitalized_english_conjunctions = [str(item).capitalize() for item in english_conjunctions]
    capitalized_english_prepositions = [str(item).capitalize() for item in english_prepositions]
    capitalized_english_determinants = [str(item).capitalize() for item in english_determinants]
    capitalized_english_pronouns = [str(item).capitalize() for item in english_pronouns]

    return capitalized_english_conjunctions \
        + capitalized_english_prepositions \
        + capitalized_english_determinants \
        + capitalized_english_pronouns


